update
	history
set
	hisPeriod = #{hisPeriod}
	,hisTitle = #{hisTitle}
	,hisContent = #{hisContent}
where
	hisId = #{hisId} and hisNo = #{hisNo}

mapper.insert:
insert into file_management (
	file_id
	, originalFileName
	, savedFileName
	, fileDivision
	, fileUserId
)
values (
	file_management_SEQ.nextval
	, #{originalFileName}
	, #{savedFileName}
	, #{fileDivision}
	, #{fileUserId}
)

	SELECT file_management_SEQ.currval FROM dual

mapper.update:
update
