	file_id = #{file_id}

mapper.insert:

insert into cert_project(
	proNum
	, proId
	, proTitle			
	, proEdu
	, proTerm
	, proProf
	, proContent
	, proFileId
)
values(
	cert_project_SEQ.nextval
	, #{proId}
	, #{proTitle}			
	, #{proEdu}
	, #{proTerm}
	, #{proProf}
	, #{proContent}
	, #{proFileId}
)

mapper.update:
update
	cert_project
set
	proTitle = #{proTitle}
	, proEdu = #{proEdu}
	, proTerm = #{proTerm}
	, proProf = #{proProf}
	, proContent = #{proContent}
	, proFileId = #{proFileId}
where
	proNum = #{proNum}
	and 
	proId = #{proId}

update CERT_CERTIFICATE
set
CERTJSON = #{certjson},
CERTHTML = #{certhtml}
where
CERTID = #{certId}
