mapper.update:
update 
	portfolio
set
	portContent = #{portContent}
where
	portNum = #{portNum}
	and
	portId = #{portId}

mapper.delete:
delete from
	portfolio
where
	portNum = #{portNum}
	and
	portId = #{portId}

mapper.insert:
insert into port_comment (
	commentNum
	, replyportNum
	, commentID
	, commentText
)
values (
	port_comment_SEQ.nextval
	, #{replyportNum}
	, #{commentID}
	, #{commentText}
 )

mapper.update:
update
		port_comment
	set
		commentText = #{commentText}
	where
		commentNum = #{commentNum}
		and replyportNum = #{replyportNum}

mapper.delete:
delete
	from
		port_comment
	where
		commentNum = #{commentNum}
		and replyportNum = #{replyportNum}
